mapper.update:
UPDATE certificates
SET
    name=#{name},
    text=#{text}
WHERE provider_code = #{providerCode}
AND service_category_code = 4

mapper.delete:
DELETE FROM beauties
WHERE beauty_code=(
    SELECT beauty_code
    FROM(
        SELECT beauty_code
        FROM beauties
        WHERE provider_code=#{providerCode}
        )tmp
    )
